import React, { useEffect } from "react";
import { useHistory } from "react-router";
import kakaologo from "./kakaologo.png";
import KakaoLogin from "./KakaoLogin";
import Axios from "axios";

const KakaoTest = () => {
  const { Kakao } = window;
  const loginWithKakao = () => {
    console.log("hello");
    Kakao.Auth.authorize({
      redirectUri: "http://localhost:3000/KakaoTest",
    });
  };

  const history = useHistory();
  const GoHome = () => history.push("/");
  const url = window.location.href;
  const arr = url.split("=");
  const authorizationCode = arr[1];

  useEffect(() => {
    getKakaoToken(authorizationCode);
  });

  const getKakaoToken = (code) => {
    Axios.post("http://localhost:8080:callback/kakao", {
      authorizationCode: code,
    }).then((res) => {
      let token = res.data.token;
      localStorage.setItem("token", token);
    });
  };

  return (
    <>
      <button onClick={GoHome}>삼보 후퇴 일보 전진</button>
      <KakaoLogin />
      <h3>{url}</h3>
      <h3>{arr}</h3>
      <h3>{arr[1]}</h3>
      <h3>{authorizationCode}</h3>
    </>
  );
};

export default KakaoTest;
